```c#
 public static string ToQueryString(this object request, string separator = &quot;,&quot;, string joiner = &quot;&amp;&quot;, bool escape = true)
{
    if (request == null)
        throw new ArgumentNullException(&quot;request&quot;);

    // Get all properties on the object
    var properties = request.GetType().GetProperties()
        .Where(x =&gt; x.CanRead)
        .Where(x =&gt; x.GetValue(request, null) != null)
        .ToDictionary(x =&gt; x.Name, x =&gt; x.GetValue(request, null));

    // Get names for all IEnumerable properties (excl. string)
    var propertyNames = properties
        .Where(x =&gt; !(x.Value is string) &amp;&amp; x.Value is IEnumerable)
        .Select(x =&gt; x.Key)
        .ToList();

    // Concat all IEnumerable properties into a comma separated string
    foreach (var key in propertyNames)
    {
        var valueType = properties[key].GetType();
        var valueElemType = valueType.IsGenericType
                                ? valueType.GetGenericArguments()[0]
                                : valueType.GetElementType();
        if (valueElemType.IsPrimitive || valueElemType == typeof(string))
        {
            var enumerable = properties[key] as IEnumerable;
            properties[key] = string.Join(separator, enumerable.Cast&lt;object&gt;());
        }
    }

    // Concat all key/value pairs into a string separated by ampersand
    return string.Join(joiner, properties
        .Select(x =&gt; string.Concat(
            (escape ? Uri.EscapeDataString(x.Key) : x.Key),
            &quot;=&quot;,
            (escape ? Uri.EscapeDataString(x.Value.ToString()) : x.Value.ToString())
            )));
}
```


