XAML

    &lt;Grid&gt;
        &lt;Rectangle x:Name=&quot;Test&quot; Width=&quot;200&quot; Height=&quot;70&quot; Fill=&quot;DarkGray&quot;&gt;&lt;/Rectangle&gt;
