        return Object.keys(obj).reduce((acc, cur) =&gt; {
            acc.push({
              id: cur,
              label: cur,
              children: recurseList(obj[cur])
            })
            return acc;
        }, [])
    }
      
    function recurseList(obj) {
        return Object.keys(obj).reduce((acc, cur) =&gt; {
            if(obj[cur] instanceof Object) {
                let data = {
                    id: cur,
                    label:cur
                }      
                const children = recurseList(obj[cur]);
                If(children.length) {
                      data.children = children
                }
                acc.push(data)
