I have tried to implement the algorithm using C++, the program consists of two routines:

1. The &quot;divide&quot; -&gt; For each array[1...n] it will recursively divide into array[1...n/2] and array[n/2+1...n] until n = 2 and fits the condition to apply bisection method (between positive and negative curve value).
2. The &quot;conquer&quot; -&gt; Basically using standard bisection method from any general numerical analysis textbook.

```#include &lt;iostream&gt;
#include &lt;vector&gt;
#include &lt;algorithm&gt;

using std::cin;
using std::cout;
using std::vector;
using std::sort;

double calculate_mid_value(const double &amp;x_l, const double &amp;x_r, const double &amp;y_l, const double &amp;y_r);
vector&lt;double&gt; calculate_roots(vector&lt;double&gt; &amp;y, const double &amp;start_value, const double &amp;end_value);

const double e_min = 0.00001;

int main()
{
    double val, start_value, end_value;
    int n;
    
    vector&lt;double&gt; curve;
    vector&lt;double&gt; roots;

    cin &gt;&gt; start_value;
    cin &gt;&gt; end_value;

    do{
        cin &gt;&gt; val;
        curve.push_back(val);
    } while(val != -999);

    curve.pop_back();

    roots = calculate_roots(curve, start_value, end_value);

    n = roots.size();

    sort(roots.begin(), roots.end());

    for(int i = 0; i &lt; n; i++)
    {
        cout &lt;&lt; roots.at(i) &lt;&lt; &#39; &#39;;
    }

    cout &lt;&lt; &#39;\n&#39;;

    return 0;
}

vector&lt;double&gt; calculate_roots(vector&lt;double&gt; &amp;y, const double &amp;start_value, const double &amp;end_value)
{
    double y_l, y_r;
    double x_l, x_r, x_m;
    double dx;
    int n;

    vector&lt;double&gt; roots;
    
    n = y.size();
    dx = (end_value - start_value) / (n - 1);

    x_l = start_value;
    x_r = end_value ;
    y_l = *y.begin();
    y_r = *y.end();

    if (n == 2 &amp;&amp; y_l * y_r &lt; 0){ 
        x_m = calculate_mid_value(x_l, x_r, y_l, y_r);

        roots.push_back(x_m);

        return roots;
    }

    else{
        vector&lt;double&gt; first_half;
        vector&lt;double&gt; second_half;
        double m, mn, val;
        int mid;
        
        
        mid = n / 2;

        //cout &lt;&lt; &quot;debug \n&quot;;

        for (int i = 0; i &lt; mid; i++){
            val = y.at(i);
            first_half.push_back(val);
        }

        for (int j = mid + 1; j &lt; n; j++){
            val = y.at(j);
            second_half.push_back(val);
        }

        m = (start_value + end_value) / 2;
        mn = m + dx;

        vector&lt;double&gt; roots_first_half;
        vector&lt;double&gt; roots_second_half;
        vector&lt;double&gt; roots_final;

        roots_first_half = calculate_roots(first_half, start_value, m);
        roots_second_half = calculate_roots(second_half, mn , end_value);

        roots_final.reserve(roots_first_half.size() + roots_second_half.size());
        roots_final.insert( roots_final.end(), roots_first_half.begin(), roots_first_half.end() );
        roots_final.insert( roots_final.end(), roots_second_half.begin(), roots_second_half.end() );

        return roots_final;
    }
}

double calculate_mid_value(const double &amp;x_l, const double &amp;x_r, const double &amp;y_l, const double &amp;y_r)
{
    double x_m = (x_r + x_l) / 2;
    double y_m = (x_m - x_l) / (x_r - x_l) * (y_r - y_l) + y_l;

    if (y_m &gt;= -e_min || y_m &lt;= e_min){
        return x_m;
    }

    else if (y_m &gt; e_min) return calculate_mid_value(x_l, x_m, y_l, y_m);

    else return calculate_mid_value(x_m, x_r, y_m, y_r);
}
