````cs
public class HomeController : Controller
{
    private readonly MyLocalizer _loc;
    public HomeController(MyLocalizer loc)
    {
        _loc = loc;
    }

    public IActionResult Index()
    {
        var msg = _loc[&quot;Welcome&quot;];
        //...
    }
}
