        cin &gt;&gt; annualInterestRate;
        cout &lt;&lt; &quot;For how many years? (15 - 30 Years): &quot;;
        cin &gt;&gt; years;
    
        float periodRate = annualInterestRate / 1200.0;
        int months = years * 12;
    
        payment = (loanAmount * periodRate * pow((1 + periodRate), months)) / (pow((1 + periodRate), months));
    
        return(payment);
    }

