```
template&lt;&gt; struct fmt::formatter&lt;S&gt; {
    enum { left, right } align = right;
    int width = 0;

    template &lt;typename ParseContext&gt;
        constexpr auto parse(ParseContext&amp; ctx)
        {
            auto it = ctx.begin();

            // parse /align/
            if (*it == &#39;&lt;&#39;) {
                align = left;
