      }
      Terms &lt;- terms(object)
      object$call$formula &lt;- object$formula &lt;- Terms
      md &lt;- missing(direction)
      direction &lt;- match.arg(direction)
      backward &lt;- direction == &quot;both&quot; | direction == &quot;backward&quot;
      forward &lt;- direction == &quot;both&quot; | direction == &quot;forward&quot;
      if (missing(scope)) {
        fdrop &lt;- numeric()
        fadd &lt;- attr(Terms, &quot;factors&quot;)
        if (md) 
