          nc &lt;- nc[!is.na(nc)][1L]
          o &lt;- order(aod[, nc])
          
          # (!) addition
          aod.all &lt;- c(aod.all, list(aod))
          
          if (trace) 
            print(aod[o, ])
          if (o[1L] == 1) 
            break
          change &lt;- rownames(aod)[o[1L]]
        }
        usingCp &lt;- match(&quot;Cp&quot;, names(aod), 0L) &gt; 0L
        fit &lt;- update(fit, paste(&quot;~ .&quot;, change), evaluate = FALSE)
        fit &lt;- eval.parent(fit)
        nnew &lt;- nobs(fit, use.fallback = TRUE)
        if (all(is.finite(c(n, nnew))) &amp;&amp; nnew != n) 
