&lt;!-- language: lang-js --&gt;

    const vid = document.querySelector(&#39;video&#39;);
    const canvas = document.getElementById(&#39;soblurme&#39;);
    const ctx = canvas.getContext(&#39;2d&#39;);

    let playing = false;

    vid.onplaying = startDrawing;
    vid.onpause = stopDrawing;

