            $ret = [];
            foreach ($result as $i =&gt; $d) $ret[ $i ] = self::convert_from_latin1_to_utf8_recursively($d);
    
            return $ret;
        } elseif (is_object($result)) {
            foreach ($result as $i =&gt; $d) $result-&gt;$i = self::convert_from_latin1_to_utf8_recursively($d);
    
