	query.setParameter(&quot;project&quot;, projectName.toLowerCase());
	query.setParameter(&quot;customer&quot;, customerName.toLowerCase());
	query.setParameter(&quot;manager&quot;, manager.toLowerCase());
	query.setParameter(&quot;wine&quot;, wineStyle.toLowerCase());
