      return data.reduce((previousPromise, ms) =&gt; {
        return previousPromise
          .then(() =&gt; {
            console.log(`sleeping for ${ms}ms`);
            return sleep(ms);
          });
      }, Promise.resolve());
    }

    async function withAsync() {
      console.log(&#39;Sequential Promises with for of and await:&#39;);

