                            rect.w += rect.x - mousePos.x;
                            rect.h += rect.y - mousePos.y;
                            rect.x = mousePos.x;
                            rect.y = mousePos.y;
                            break;
                        case &#39;topright&#39;:
                            rect.w = mousePos.x - rect.x;
                            rect.h += rect.y - mousePos.y;
                            rect.y = mousePos.y;
                            break;
                        case &#39;bottomleft&#39;:
                            rect.w += rect.x - mousePos.x;
                            rect.x = mousePos.x;
                            rect.h = mousePos.y - rect.y;
                            break;
                        case &#39;bottomright&#39;:
                            rect.w = mousePos.x - rect.x;
                            rect.h = mousePos.y - rect.y;
                            break;

                        case &#39;top&#39;:
                            rect.h += rect.y - mousePos.y;
                            rect.y = mousePos.y;
                            break;

                        case &#39;left&#39;:
                            rect.w += rect.x - mousePos.x;
                            rect.x = mousePos.x;
                            break;

                        case &#39;bottom&#39;:
                            rect.h = mousePos.y - rect.y;
                            break;

                        case &#39;right&#39;:
                            rect.w = mousePos.x - rect.x;
                            break;
                    }
                }
                if (drag || currentHandle != previousHandle)
                    draw();
            }

            function draw() {
                ctx.clearRect(0, 0, canvas.width, canvas.height);
                ctx.fillStyle = &#39;black&#39;;
                ctx.fillRect(rect.x, rect.y, rect.w, rect.h);
                if (currentHandle) {
                    var posHandle = point(0, 0);
                    switch (currentHandle) {
                        case &#39;topleft&#39;:
                            posHandle.x = rect.x;
                            posHandle.y = rect.y;
                            break;
                        case &#39;topright&#39;:
                            posHandle.x = rect.x + rect.w;
                            posHandle.y = rect.y;
                            break;
                        case &#39;bottomleft&#39;:
                            posHandle.x = rect.x;
                            posHandle.y = rect.y + rect.h;
                            break;
                        case &#39;bottomright&#39;:
                            posHandle.x = rect.x + rect.w;
                            posHandle.y = rect.y + rect.h;
                            break;
                        case &#39;top&#39;:
                            posHandle.x = rect.x + rect.w / 2;
                            posHandle.y = rect.y;
                            break;
                        case &#39;left&#39;:
                            posHandle.x = rect.x;
                            posHandle.y = rect.y + rect.h / 2;
                            break;
                        case &#39;bottom&#39;:
                            posHandle.x = rect.x + rect.w / 2;
                            posHandle.y = rect.y + rect.h;
                            break;
                        case &#39;right&#39;:
                            posHandle.x = rect.x + rect.w;
                            posHandle.y = rect.y + rect.h / 2;
                            break;
                    }
                    ctx.globalCompositeOperation = &#39;xor&#39;;
                    ctx.beginPath();
                    ctx.arc(posHandle.x, posHandle.y, handlesSize, 0, 2 * Math.PI);
                    ctx.fill();
                    ctx.globalCompositeOperation = &#39;source-over&#39;;
                }
            }

            init();
            draw();
        &lt;/script&gt;


