    /* ============================================================================
      &lt;SupplierForm /&gt;
    ============================================================================= */
    class SupplierForm extends React.Component&lt;Props, State&gt; {
      static defaultProps = { editData: null };
    
      constructor() {
        super();
        this.state = {
          companyname: &#39;&#39;,
          supplier_type_id: 0,
          country_id: 0,
          phonenumber: &#39;&#39;,
          phonenumber1: &#39;&#39;,
          email: &#39;&#39;,
          contact_person: &#39;&#39;,
          skype_id: &#39;&#39;,
          notes: &#39;&#39;,
        };
      }
    
      componentDidMount() {
        const { editData, action } = this.props;
        if (action === &#39;edit&#39;) {
          const record = { ...editData };
          const {
            companyname,
            supplier_type_id,
            country,
            phonenumber,
            phonenumber1,
            landlord_email,
            contact_person,
            landlord_skype_id,
            notes,
          } = record;
          this.setState({
            companyname,
            supplier_type_id,
            country_id: country,
            phonenumber,
            phonenumber1,
            email: landlord_email,
            contact_person,
            skype_id: landlord_skype_id,
            notes,
          });
        }
      }
    
      // for submit form
      _handleSubmit = () =&gt; {
        const { onError, onSubmit } = this.props;
        const {
          companyname,
          supplier_type_id,
          country_id,
          phonenumber,
          phonenumber1,
          email,
          contact_person,
          skype_id,
          notes,
        } = this.state;
        const supplier = {
          companyname,
          supplier_type_id,
          country_id,
          phonenumber,
          phonenumber1,
          email,
          contact_person,
          skype_id,
          notes,
        };
        if (!companyname) {
          onError(&#39;error&#39;, &#39;Please Enter Company Name&#39;);
        } else if (!Number(supplier_type_id)) {
          onError(&#39;error&#39;, &#39;Please Select Supplier Type&#39;);
        } else if (!Number(supplier_type_id)) {
          onError(&#39;error&#39;, &#39;Please Select Country&#39;);
        } else if (!email) {
          onError(&#39;error&#39;, &#39;Please Enter Email&#39;);
        } else {
          onSubmit(supplier);
        }
      };
    
      /**
       * handle picker values
       * @param {string} fieldName
       * @param {string} value
       */
      _handlePickerValue = (pickerName: string, _id: string) =&gt; {
        this.setState({
          [`${pickerName}_id`]: _id,
        });
      };
    
      /**
       * handle input values
       * @param {string} fieldName
       * @param {string} value
       */
      _handleInputValue = (fieldName: string, value: string) =&gt; {
        this.setState({ [fieldName]: value });
      };
    
      /**
