  &lt;!-- This attribute is &quot;false&quot; by default on apps targeting
       Android 10 or higher. --&gt;
  &lt;application 
       android:requestLegacyExternalStorage=&quot;true&quot; ........ &gt;
    ......
  &lt;/application&gt;
&lt;/manifest&gt;
