        tupleType zippedIterators = make_tuple(distances.begin(), distances_to_cluster);
        
        typedef zip_iterator&lt; tupleType &gt; zipType;
        zipType zip(zippedIterators);
        
        typedef transform_output_iterator&lt; UnaryFunctor_get_cluster_number_from_tuple, device_vector&lt;size_t&gt;::iterator &gt; transformOutputType;
        transformOutputType transform_output(clustering_result.begin(), UnaryFunctor_get_cluster_number_from_tuple());
        
        reduce_by_key(device, reduce_map, reduce_map + n*m, zip, make_discard_iterator(), transform_output, 
                      BinaryPredicate(), BinaryFunctor_get_minimal_distance_tuple());
