    #sidebar-user-box:hover {
      background-color: #999999;
    }

    #sidebar-user-box:after {
      content: &quot;.&quot;;
      display: block;
      height: 0;
      clear: both;
      visibility: hidden;
    }

    img {
      width: 35px;
      height: 35px;
      border-radius: 50%;
      float: left;
    }

    #slider-username {
      float: left;
      line-height: 30px;
      margin-left: 5px;
    }

