eval(&quot;module.exports = __webpack_require__.p + \&quot;assets/logo_arc.png\&quot;;//# sourceURL=[module]\n//# sourceMappingURL=data:application/json;charset=utf-8;base64,\n//# sourceURL=webpack-internal:///./src/assets/logo_arc.png\n&quot;);

