    // hanoi :: Integer -&gt; [Char] -&gt; [Char] -&gt; [Char] -&gt; IO ()
    void hanoi(int64_t n, char *f, char *h, char *t)
    {
        if (0 == n) {
            // hanoi 0 f _ _ = do { putStrLn (&quot;Lane \&quot;&quot;++ f ++ &quot;\&quot; empty!&quot;) }
            printf(&quot;Lane \&quot;%s\&quot; empty!\n&quot;, f);
        } else {
            // hanoi n f h t =
            hanoi(n-1,f,t,h); // hanoi (n - 1) f t h
            move(f,t);        // move f t
            hanoi(n-1,h,f,t); // hanoi (n - 1) h f t
        }
    }
    
    int main()
