--The query

	 SELECT A.ID
		   ,B.*
	 FROM OPENJSON (@ResponseText, &#39;$.submissions&#39;) 
		  WITH (ID NVARCHAR(100) &#39;$.xml_id&#39;
			   ,fields NVARCHAR(MAX) AS JSON) A
	 OUTER APPLY OPENJSON(a.fields)
				 WITH(fieldvalue NVARCHAR(150)
					 ,fieldid BIGINT) B;

