        var rows = document.querySelectorAll(&#39;tr&#39;);

        for (var i = 0; i &lt; rows.length; i++) {
          var redCells = rows[i].querySelectorAll(&#39;td[class*=&quot;red&quot;]&#39;);
          
          if (redCells.length === 4) {
            alert(&quot;That&#39;s 4!&quot;);
          }
