    #!/usr/bin/python3.4
    import re

    posts = [
        &quot;hi #weekend&quot;,
        &quot;good morning #zurich #limmat&quot;,
        &quot;spend my #weekend in #zurich&quot;,
        &quot;#zurich &lt;3&quot;]

    container = {}
    for post in posts:
        elements = re.findall(&#39;#(\w+)&#39;, post)
        for element in elements:
            if container.get(element, None):
                container[element] += 1
            else:
                container[element] = 1
    print(container)

