        return (c &lt; &#39;a&#39; || c &gt; &#39;z&#39;) 
            &amp;&amp; (c &lt; &#39;A&#39; || c &gt; &#39;Z&#39;)
            &amp;&amp; (c &lt; &#39;0&#39; || c &gt; &#39;9&#39;)
            &amp;&amp; (c != &#39; &#39;);
    }
    
    std::string StripDuplicates(const std::string&amp; str) {
    
       std::bitset&lt;256&gt; alreadySeen;
    
       std::string result;
    
       for (const unsigned char&amp; c : str) {
          if (!alreadySeen[c] &amp;&amp; IsSpecialChar(c)) {
             result += c;
             alreadySeen[c] = true;
          }
       }
    
       return result;
    }
    
