```

App.js (the state and state management is kept in App.js )
If you don&#39;t know react useState, look at the react documentation.

```
import React, { useState } from &quot;react&quot;;
import ReactDOM from &quot;react-dom&quot;;
import Post from &quot;./Post&quot;;

const initialPosts = [
  { id: 1, text: &quot;Post 1&quot;, likeCount: 0 },
  { id: 2, text: &quot;Post 2&quot;, likeCount: 0 },
  { id: 3, text: &quot;Post 3&quot;, likeCount: 0 }
];

function App() {
  const [posts, setPosts] = useState(initialPosts);

  const like = id =&gt; {

    const updatedPosts = posts.map(post =&gt; {
      if (post.id === id) {
        return { ...post, likeCount: post.likeCount +1 };
      } else {
        return post;
      }
    });
    setPosts(updatedPosts);
  };

  const dislike = id =&gt; {
    const updatedPosts = posts.map(post =&gt; {
      if (post.id === id) {
        return { ...post, likeCount: post.likeCount -1 };
      } else {
        return post;
      }
    });

    setPosts(updatedPosts);
  };
  
  return (
    &lt;div className=&quot;App&quot;&gt;
      {posts.map(post =&gt; (
        &lt;Post key={post.id} post={post} like={like} dislike={dislike} /&gt;
      ))}
    &lt;/div&gt;
  );
}

```

