    int sD = 4;
    int ledB = 2;
    int ledG = 3;
    int sU = 5;
    int mainSwitch = 7;
    int ledBin = 8;
    int ledGin = 9;

    void setup() 
    {
    
     pinMode(2, OUTPUT);
     pinMode(3, OUTPUT);
     pinMode(4, INPUT);
     pinMode(5, INPUT);
     pinMode(7, INPUT_PULLUP);
     pinMode(8, INPUT);
     pinMode(9, INPUT);
    }

    void loop() 
    {

     mainSwitch = digitalRead(7);

     if (mainSwitch == true) //Ends Program
      {
       sD = digitalRead(4);
       sU = digitalRead(5);

       digitalWrite(ledB, LOW);
       digitalWrite(ledG, LOW);
      }


     if (mainSwitch == false)//Starts program 
     {
      sU = digitalRead(5);
      sD = digitalRead(4);
    
      if (sU == HIGH) //Turns on ledG(down solenoid) at first start. sU(upper 
      sensor) in deactivated
      {
