    	int arr[][N] = 
    	{
            { 1, 2, 3 } ,
            { 4, 5, 6 } ,
            { 7, 8, 9 }
        };
        
    	print( arr ) &lt;&lt; &#39;\n&#39;;
    	
    	return 0;
