                    s(32 downto 17) := b(15 downto 0) + a(15 downto 0);
                end if;
                s(31 downto 0) := s(32 downto 1);
            end if;
            if incre = 16 then
                P(31 downto 0) &lt;= s(32 downto 1);
                valid1 := &#39;1&#39;;
                valid &lt;= &#39;1&#39;;
                incre := 0;
            end if;
        end process;
    end architecture;

and the testbench

