          return value;
        });
        
        let sineX = d3.scaleLinear()
        	.domain([-1, 1])
        	.range([0, width])
    		
        let sineY = d3.scaleLinear()
        	.domain(d3.extent(sineData, function(d) {
       			 return d[0]
          }))
