**[SQL DEMO][1]**

    CREATE OR REPLACE FUNCTION rec_insert()
      RETURNS trigger AS
    $$
    BEGIN
         IF TG_OP = &#39;INSERT&#39; THEN     
           INSERT INTO t2 (id, c1, c2)  VALUES (new.ID, new.c1, new.c2);
         ELSE
           UPDATE t2
           SET c1 = new.c1
    	     , c2 = new.c2
           WHERE id = new.ID;
         END IF;
     
        RETURN NEW;
    END;
    $$
    LANGUAGE plpgsql;

    CREATE TRIGGER ins_same_rec
      AFTER INSERT OR UPDATE
      ON t1
      FOR EACH ROW
      EXECUTE PROCEDURE rec_insert();


