      if index(date,&#39;/&#39;) then date_ck = input(cats(date,&#39;/&#39;,year(today())),mmmddyy10.);
      else date_ck = input(cats(date,year(today())),date9.);
      format date_ck date9.;
      new_date = substr(put(date_ck,date9.),1,5);
    run;

