        @Benchmark
        @Fork(1)
        public List&lt;Integer&gt; inc() {
            List&lt;Integer&gt; answers = new ArrayList&lt;&gt;();
            for (int i = 3; i &lt; 100; i++) {
                int bit = i % 32;
                int cur = i / 32;
                long test = (primes[cur] &gt;&gt; bit) &amp; 1;
                if (test == 1) {
                    answers.add(i);
                }
            }
            return answers;
        }
        
        @Benchmark
        @Fork(1)
        public List&lt;Integer&gt; addOne() {
            List&lt;Integer&gt; answers = new ArrayList&lt;&gt;();
            for (int i = 3; i &lt; 100; i+=1) {
                int bit = i % 32;
                int cur = i / 32;
                long test = (primes[cur] &gt;&gt; bit) &amp; 1;
                if (test == 1) {
                    answers.add(i);
                }
            }
            return answers;
        }
        
        @Benchmark
        @Fork(1)
        public List&lt;Integer&gt; addTwo() {
            List&lt;Integer&gt; answers = new ArrayList&lt;&gt;();
            for (int i = 3; i &lt; 100; i+=2) {
                int bit = i % 32;
                int cur = i / 32;
                long test = (primes[cur] &gt;&gt; bit) &amp; 1;
                if (test == 1) {
                    answers.add(i);
                }
            }
            return answers;
        }
    }


