		for (INT_PTR i = 0; i &lt; m_nSize; i++)
			ar &lt;&lt; m_pData[i];
	}
	else
	{
		DWORD_PTR nOldSize = ar.ReadCount();
		SetSize(nOldSize);
		for (INT_PTR i = 0; i &lt; m_nSize; i++)
			ar &gt;&gt; m_pData[i];
