    ];

    var i = 0;
    $(&quot;#claim&quot;).html(classes[i]);

    var interval = setInterval(function() {       
      if(i == classes.length - 2){ // stop at the end item
        clearInterval(interval);
      }
      i = (i + 1) % classes.length;
      $(&quot;#claim&quot;).html(classes[i]);

    },  800);

