    UNION ALL SELECT  23,&#39;4th approved version&#39;
    UNION ALL SELECT  24,&#39;5th approved version&#39;
    )
    ,
    account(accountopeneddate) AS (
    SELECT &#39;2019-08-15&#39;::DATE
