The only way I&#39;m aware of about playing video in sync is to emit a custom event to sync the video in requestAnimationFrame:

&lt;!-- begin snippet: js hide: false console: true babel: false --&gt;

&lt;!-- language: lang-js --&gt;

    var videos = {
        a: Popcorn(&quot;#a&quot;),
        b: Popcorn(&quot;#b&quot;),
      },
      scrub = $(&quot;#scrub&quot;),
      loadCount = 0,
      events = &quot;play pause timeupdate seeking&quot;.split(/\s+/g);

    // iterate both media sources
    Popcorn.forEach(videos, function(media, type) {

      // when each is ready... 
      media.on(&quot;canplayall&quot;, function() {

        // trigger a custom &quot;sync&quot; event
        this.emit(&quot;sync&quot;);

        // set the max value of the &quot;scrubber&quot;
        scrub.attr(&quot;max&quot;, this.duration());

        // Listen for the custom sync event...    
      }).on(&quot;sync&quot;, function() {

        // Once both items are loaded, sync events
        if (++loadCount == 2) {

          // Iterate all events and trigger them on the video B
          // whenever they occur on the video A
          events.forEach(function(event) {

            videos.a.on(event, function() {

              // Avoid overkill events, trigger timeupdate manually
              if (event === &quot;timeupdate&quot;) {

                if (!this.media.paused) {
                  return;
                }
                videos.b.emit(&quot;timeupdate&quot;);

                // update scrubber
                scrub.val(this.currentTime());

                return;
              }

              if (event === &quot;seeking&quot;) {
                videos.b.currentTime(this.currentTime());
              }

              if (event === &quot;play&quot; || event === &quot;pause&quot;) {
                videos.b[event]();
              }
            });
          });
        }
      });
    });

    scrub.bind(&quot;change&quot;, function() {
      var val = this.value;
      videos.a.currentTime(val);
      videos.b.currentTime(val);
    });

    // With requestAnimationFrame, we can ensure that as 
    // frequently as the browser would allow, 
    // the video is resync&#39;ed.
    function sync() {
      if (videos.b.media.readyState === 4) {
        videos.b.currentTime(
          videos.a.currentTime()
        );
      }
      requestAnimationFrame(sync);
    }

    sync();

