const renderName = ({ name, children, index }) =&gt; {
  console.log(name);
  return (
    &lt;div key={index}&gt;
      &lt;p&gt;{name}&lt;/p&gt;
      {children &amp;&amp; children.map((item, index)=&gt; {
        const { name } = item;
        return renderName({name, index})
      })}
      {/* {children &amp;&amp; children.forEach(newData =&gt; renderName(newData))} */}
    &lt;/div&gt;
  );
};
```

