      if (str.includes(&#39;\n&#39;)) {
        let firstChar = str.slice(0, 1);
        if (parseInt(firstChar)) {
          return str.split(/,|\n/);
        } else {
          let regex = new RegExp(`,|\\n|\\${firstChar}`, &#39;g&#39;) // building a dynamic regex here
