public ObjectMetadata get(final S3DAOInput daoInput, final File destinationFile) {
        final ObjectMetadata metadata = this.getS3Client().getObjectMetadata(daoInput.getBucketName(), daoInput.getKey());

        final long length = metadata.getContentLength();
        long startIndex = 0L;
        long endIndex = (length &lt; this.maxBytesToReadPerSecondPerThread) ? length - 1 : this.maxBytesToReadPerSecondPerThread - 1;

        /**
         * Write in multiple of 4096 for better performance.
         * https://docs.aws.amazon.com/AWSEC2/latest/WindowsGuide/storage-optimized-instances.html
         */
        final int bufferSize = 4096 * 16;
        final byte[] buffer = new byte[bufferSize];
        createParentDirectoryIfNecessary(destinationFile);

        try (OutputStream output = new FileOutputStream(destinationFile)) {
            while (startIndex &lt; length) {
                long maxBytesToRead = endIndex - startIndex + 1;
                this.rateLimiter.acquire((int) (maxBytesToRead));
                final GetObjectRequest getObjectRequest = new GetObjectRequest(daoInput.getBucketName(),
                        daoInput.getKey());
                if (length - startIndex &lt; this.maxBytesToReadPerSecondPerThread) {
                    getObjectRequest.setRange(startIndex);
                } else {
                    getObjectRequest.setRange(startIndex, endIndex);
                }

                try (S3Object s3Object = this.getS3Client().getObject(getObjectRequest)) {
                    // We are reading 200 MB which will fit in int. We can read at max till 2GB to not break.
                    int bytesReadTotal = (int) IOUtils.copyLarge(s3Object.getObjectContent(), output, buffer);
                    startIndex = startIndex + bytesReadTotal;
                    endIndex = Math.min(endIndex + this.maxBytesToReadPerSecondPerThread, length);
                }
            }
        } catch (final IOException e) {
            throw new RuntimeException(e);
        }

        return metadata;
    }
