                &#39;Each snap value needs to be within slider values range.&#39;),
            super(key: key);
    
      final Key sliderKey;
    
      final Set&lt;double&gt; snapValues;
    
      final double value;
    
      final ValueChanged&lt;double&gt; onSnapValueChanged;
    
      final double snapDistance;
    
      final Curve animCurve;
    
