      final double min;
    
      final double max;
    
      final String label;
    
      final int divisions;
    
      final Color activeColor;
    
      final Color inactiveColor;
    
      final ValueChanged&lt;double&gt; onChanged;
    
      final ValueChanged&lt;double&gt; onChangeEnd;
    
      final ValueChanged&lt;double&gt; onChangeStart;
    
      final SemanticFormatterCallback semanticFormatterCallback;
    
      @override
      _StepSliderState createState() =&gt; _StepSliderState();
    }
    
    class _StepSliderState extends State&lt;SnapSlider&gt;
        with SingleTickerProviderStateMixin {
      AnimationController _animator;
      CurvedAnimation _baseAnim;
      Animation&lt;double&gt; _animation;
      double _lastSnapValue;
    
      @override
      void didUpdateWidget(SnapSlider oldWidget) {
        super.didUpdateWidget(oldWidget);
        _animator.duration = widget.animDuration;
        _baseAnim.curve = widget.animCurve;
      }
    
      @override
      void initState() {
        super.initState();
        _animator = AnimationController(
            vsync: this, duration: widget.animDuration, value: 1.0);
        _baseAnim = CurvedAnimation(parent: _animator, curve: widget.animCurve);
        _recreateAnimation(widget.value, widget.value);
        _animation.addListener(() {
          _onSliderChanged(_animation.value);
          widget.onChanged?.call(_animation.value);
        });
      }
    
      @override
      void dispose() {
        _animator.dispose();
        super.dispose();
      }
    
      @override
      Widget build(BuildContext context) {
        return AnimatedBuilder(
          animation: _animator,
          builder: (_, __) =&gt; Slider(
            key: widget.sliderKey,
            min: widget.min,
            max: widget.max,
            label: widget.label,
            divisions: widget.divisions,
            activeColor: widget.activeColor,
            inactiveColor: widget.inactiveColor,
            semanticFormatterCallback: widget.semanticFormatterCallback,
            value: widget.value,
            onChangeStart: (it) {
              _animator.stop();
              _onSliderChangeStart(it);
              widget.onChangeStart?.call(it);
            },
            onChangeEnd: (it) {
              _onSliderChangeEnd(it);
              widget.onChangeEnd?.call(it);
            },
            onChanged: (it) {
              _onSliderChanged(it);
              widget.onChanged?.call(it);
            },
          ),
        );
      }
    
      void _onSliderChangeStart(double value) {
      }
    
      void _onSliderChangeEnd(double value) {
        double snapValue = _closestSnapValue(value);
        var distance = (value - snapValue).abs();
        if (snapValue != _lastSnapValue) {
          if (distance &lt;= widget.snapDistance) {
            _animateTo(widget.value, snapValue, true);
            widget.onSnapValueChanged?.call(widget.value);
            _lastSnapValue = snapValue;
          }
        } else {
          if (distance &gt; widget.snapDistance) {
            _lastSnapValue = null;
          }
        }
      }
    
      double _closestSnapValue(double value) {
        return widget.snapValues.reduce((a, b) {
          var distanceA = (value - a).abs();
          var distanceB = (value - b).abs();
          return distanceA &lt; distanceB ? a : b;
        });
      }
    
      void _onSliderChanged(double value) {
      }
    
      void _animateTo(double start, double end, bool restart) {
        _recreateAnimation(start, end);
        _animator.forward(from: 0.0);
      }
    
      void _recreateAnimation(double start, double end) {
        _animation = Tween(begin: start ?? end, end: end).animate(_baseAnim);
      }
    }

So, it appears (based on experimentation as this is not in the doc), that when you pass an array of routes, Express finds the first item in the array that matches the current path and calls the route handler for that one only.  Even if there are other routes that would also match (as in your case), it does not call the route handler again for those.

So, because `&#39;/&#39;` matches ALL routes when used with `app.use()`, it will always use that one and thus the others will never work properly when using the array, but do work properly when listed as individual routes.  You can fix it by making sure that the most permissive routes are last.  So, change your array to this:

    const static = express.static(path.join(__dirname, &#39;../build&#39;));
    const routes = [&#39;/projects&#39;,&#39;/project1&#39;,&#39;/blog&#39;, &#39;/&#39;];
    app.use(routes, static);

This moves `&#39;/&#39;` to the end so the others get a chance to match before the `&#39;/&#39;` is looked at and .  `&#39;/&#39;` will only get used when the path wasn&#39;t one of the others.

----------

FYI, this is a bit of a weird structure where you&#39;re directing multiple URL path prefixes all to the same `../build` directory for `express.static()` to look there.  There&#39;s no reason you need to do it that way.  This appears to be an artificially created issue because of your URL design.  Doing it this way will cause URLs like `/x.js`, `/projects/x.js` and `/blog/x.js` to resolve to the exact same `x.js` file in the `../build` directory.

