        let percentage;
        if (bill &lt; 50) {
          percentage = (20 / 100);
        } else if (bill &gt;= 50 &amp;&amp; bill &lt; 200) {
          percentage = (15 / 100);
        } else {
          percentage = (10 / 100);
        }
