                  installComponentLoader.style.display = &#39;flex&#39;;
                  installComponentLoader.style.visibility = &#39;visible&#39;;
                }
              }).finished,

              anime({
                targets: `#${currentRowID} .component-name`,
                opacity: 0.5,
                duration: 500,
                easing: &#39;linear&#39;,
              }).finished
            ]);
          };
          animateLoaderAndText().then(() =&gt; {
            anime({
              targets: `#${currentRowID} #delete-component`,
              opacity: 1,
              complete: (animation) =&gt; {
                const removeComponent = document.querySelector(`#${currentRowID} #delete-component`);
                removeComponent.style.display = &#39;block&#39;;
                removeComponent.style.visibility = &#39;visible&#39;;
              }
            });

            anime({
              targets: `#${currentRowID} .component-name`,
              opacity: 1,
              duration: 250,
              easing: &#39;linear&#39;,
            });
          })
        }).catch((reason) =&gt; {
          async function hideAnimateLoaderAndShowText() {
            await Promise.all([
              anime({
                targets: `#${currentRowID} .loader-holder`,
                opacity: 0,
                duration: 500,
                easing: &#39;cubicBezier(.62,-0.4,.45,.85)&#39;,
                complete: (animation) =&gt; {
                  const installComponentLoader = document.querySelector(`#${currentRowID} .loader-holder`);
                  installComponentLoader.style.display = &#39;none&#39;;
                  installComponentLoader.style.visibility = &#39;hidden&#39;;
                }
              }).finished,

              anime({
                targets: `#${currentRowID} .component-name`,
                opacity: 1,
                duration: 500,
                easing: &#39;linear&#39;,
              }).finished
            ]);
          }

          hideAnimateLoaderAndShowText().then(() =&gt; {
            const xMax = 6;
            anime({
              targets: `#${currentRowID}`,
              duration: 750,
              easing: &#39;easeInOutSine&#39;,
              translateX: [{
                  value: xMax * -1
                },
                {
                  value: xMax
                },
                {
                  value: xMax * -1
                },
                {
                  value: xMax
                },
                {
                  value: xMax * -1
                },
                {
                  value: 0
                },
              ]
            });
            anime({
              targets: `#${currentRowID} .install-component`,
              translateX: &#39;0px&#39;,
              opacity: 1,
              duration: 500,
              easing: &#39;cubicBezier(.62,-0.4,.45,.85)&#39;,
              begin: (animation) =&gt; {
                const installComponentButton = document.querySelector(`#${currentRowID} .install-component`);
                installComponentButton.style.visibility = &#39;visible&#39;;
                installComponentButton.style.display = &#39;block&#39;;
              },
            });
          });
        })
      });
    });

&lt;!-- language: lang-css --&gt;

    /* resets */

    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
      margin: 0;
      padding: 0;
    }

    ul,
    li {
      margin: 0;
      padding: 0;
    }

    button {
      margin: 0;
      padding: 0;
    }

    #body-pretty-much {
      height: 100%;
      width: 100%;
      position: relative;
    }

    @keyframes slideFromBottom {
      0% {
        transform: translate3d(-50%, -15%, 0) rotate3d(0, 0, 1, -3deg);
      }
      100% {
        transform: translate3d(-50%, calc(-50% - 0.5px), 0) rotate3d(0, 0, 1, 0deg);
      }
    }

    #smaller-container {
      display: flex;
      flex-direction: column;
      flex-wrap: wrap;
      position: fixed;
      top: 50%;
      left: 50%;
      animation-duration: 0.85s;
      animation-fill-mode: forwards;
      animation-iteration-count: 1;
      animation-timing-function: cubic-bezier(.33, .01, .19, .96);
      animation-name: slideFromBottom;
      width: 580px;
      min-height: 400px;
      padding: 24px;
      max-width: 100%;
      background-color: white;
      will-change: transform, opacity, height;
      transition: height 0.35s ease;
      border-radius: 5px;
    }

    .demo-install-components {
      display: flex;
      flex-direction: column;
    }

    @keyframes pulsate {
      0% {
        opacity: 1;
        transform: scale(0);
      }
      100% {
        opacity: 0;
        transform: scale(1.6);
      }
    }

    .loader-container {
      position: absolute;
      right: 24px;
    }

    .loader-holder {
      display: flex;
      flex-direction: row;
      justify-content: center;
      align-items: center;
      position: absolute;
      z-index: 2;
      width: 20px;
      height: 20px;
    }

    .loader-holder:before {
      display: flex;
      content: &#39;&#39;;
      position: absolute;
      z-index: 1;
      will-change: transform, opacity;
      background-color: transparent;
      width: 70%;
      height: 70%;
      border: 2px solid rgba(0, 0, 0, 1);
      border-radius: 50%;
      transform: scale(0);
      animation: 1.75s ease 0s pulsate infinite;
      animation-delay: 0.5s;
    }

    .loader-holder:after {
      display: flex;
      content: &#39;&#39;;
      position: absolute;
      z-index: 2;
      will-change: transform, opacity;
      background-color: transparent;
      width: 100%;
      height: 100%;
      border: 2px solid rgba(0, 0, 0, 1);
      border-radius: 50%;
      transform: scale(0);
      animation: 1.75s ease 0s pulsate infinite;
      animation-delay: 0.25s;
    }

    #components-to-install-list {
      display: flex;
      flex-direction: row;
      flex-wrap: wrap;
      width: 100%;
    }

    .install-component-individual {
      display: flex;
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
      padding: 12px 0;
      border-bottom: 1px solid rgba(0, 0, 0, 0.1);
      width: 100%;
      position: relative;
      transform: translateX(-15px);
      animation: LeftfancyFadeInstallComponent 0.35s cubic-bezier(0.5, 0, 0.5, 1) forwards, fancyFadeInstallComponentOpacity 0.25s linear forwards;
    }

    .install-component-individual:nth-last-child(-n+2) {
      border: 0;
    }

    .install-component-individual:nth-child(even) {
      transform: translateX(15px);
      animation: RightfancyFadeInstallComponent 0.35s cubic-bezier(0.5, 0, 0.5, 1) forwards, fancyFadeInstallComponentOpacity 0.25s linear forwards;
    }

    @keyframes fancyFadeInstallComponentOpacity {
      0% {
        opacity: 0;
      }
      100% {
        opacity: 100;
      }
    }

    @keyframes LeftfancyFadeInstallComponent {
      0% {
        transform: translateX(-15px);
      }
      100% {
        transform: translateX(0);
      }
    }

    @keyframes RightfancyFadeInstallComponent {
      0% {
        transform: translateX(15px);
      }
      100% {
        transform: translateX(0);
      }
    }

    .demo-install-components {
      display: flex;
      flex-direction: column;
      flex-wrap: wrap;
    }

    .install-all-components {
      border: 0;
      padding: 12px;
      margin: 24px 0 0 0;
      text-transform: uppercase;
      font-size: 12px;
      font-weight: 600;
      color: #404040;
      letter-spacing: 3px;
      background-color: #78f5ff;
      width: 100%;
    }

    .install-all-components:hover {
      cursor: pointer;
    }

    .install-component {
      background: none;
      border: 0;
      padding: 0;
      margin: 0;
      font-weight: 600;
      font-size: 11px;
      letter-spacing: 2px;
      text-transform: uppercase;
      color: #656565;
      opacity: 1;
      visibility: visible;
      transform: translateX(0);
    }

    .install-component:hover {
      cursor: pointer;
    }

    .component-name {
      color: black;
      font-size: 16px;
      font-weight: 500;
    }

    #delete-component {
      display: block;
      position: absolute;
      top: 50%;
      right: 0;
      transform: translate3d(0, -50%, 0);
      width: 20px;
      height: 20px;
      opacity: 0;
      visibility: hidden;
    }

    .install-component-individual .loader-holder {
      display: none;
      top: 50%;
      right: 0;
      transform: translate3d(0, -50%, 0);
      width: 15px;
      height: 15px;
      opacity: 0;
      visibility: hidden;
    }


    /* Base classes to handle state changes */

    *[data-dynamic-state=&quot;hidden&quot;] {
      display: none !important;
    }

    *[data-dynamic-state=&quot;visible&quot;] {
      display: inherit !important;
    }

