    let observer = new IntersectionObserver(entries =&gt; {
      if (entries[0].boundingClientRect.y &lt; 0) {
        console.log(&quot;Past the yellow div!&quot;);
      } else {
        console.log(&quot;Not past the yellow div&quot;);
      }
    });
    observer.observe(document.querySelector(&quot;#pixelToWatch&quot;));

