&lt;!-- language: lang-js --&gt;

    $(&#39;.closeInfo, .openInfo&#39;).on(&#39;click&#39;, function() {
      let $closestEvent = $(this).closest(&#39;.event&#39;);
      $closestEvent.find(&#39;.eventInfo, .closeInfo, .openInfo&#39; ).toggle();
    });

&lt;!-- language: lang-css --&gt;

    .event {
