        state = (KeyValueStore&lt;Integer, Integer&gt;) context.getStateStore(stateStoreName);
        context.schedule(
            Duration.ofMinutes(1), 
            PunctuationType.STREAM_TIME, 
            timestamp -&gt; {
                // You can get aggregates from the state store here 

