  homeButton: {
    background: &quot;white&quot;,
    color: &quot;#09a1e2&quot;,
    border: &quot;1px solid #09a1e2&quot;,
    padding: &quot;.5rem 3.25rem .5rem 3.25rem&quot;,
    textDecoration: &quot;none&quot;,
