      &quot;definitions&quot;: {
        &quot;containtsAsia&quot;: {
          &quot;contains&quot;: {
            &quot;properties&quot;: {
              &quot;region&quot;: {
                &quot;const&quot;: &quot;asia&quot;
              }
            }
          }
        },
        &quot;containsEurope&quot;: {
          &quot;contains&quot;: {
            &quot;properties&quot;: {
              &quot;region&quot;: {
                &quot;const&quot;: &quot;europe&quot;
              }
            }
          }
        }
      },
      &quot;type&quot;: &quot;object&quot;,
      &quot;properties&quot;: {
        &quot;stat_data&quot;: {
          &quot;type&quot;: &quot;array&quot;,
          &quot;minItems&quot;: 1,
          &quot;items&quot;: {
            &quot;type&quot;: &quot;object&quot;,
            &quot;properties&quot;: {
              &quot;region&quot;: {
                &quot;enum&quot;: [
                  &quot;america&quot;,
                  &quot;asia&quot;,
                  &quot;europe&quot;
                ]
              },
              &quot;country&quot;: {
                &quot;type&quot;: &quot;string&quot;
              },
              &quot;population&quot;: {
                &quot;type&quot;: &quot;string&quot;
              }
            }
          },
          &quot;oneOf&quot;: [
            {
              &quot;allOf&quot;: [
                {
                  &quot;$ref&quot;: &quot;#/definitions/containtsAsia&quot;
                },
                {
                  &quot;not&quot;: {
