```js
type ToggleModalAction = {type: &#39;TOGGLE_MODAL&#39;};
type CancelRequestAction = {type: &#39;CANCEL_REQUEST&#39;, payload: boolean};
type UpdateCompanyAction = {type: &#39;UPDATE_COMPANY&#39;, payload: number};
