        group.oldest = Math.max(...group.members.map(member =&gt; member.age));
        group.sum = group.sum + curr.age; 
      } else {
        acc.noOfGroups = acc.noOfGroups + 1 || 1;
        let groupName = &quot;group&quot; + acc.noOfGroups;
        acc[groupName] = {
